import React from "react";
import ReactDOM from "react-dom";
import Application from "@anu/Application";
import ToolboxPage from "../pages/toolbox";
import { transformToolbox } from "../utilities/transform.toolbox";

document.addEventListener("DOMContentLoaded", () => {
  // Get courses page element and its content generated by Drupal.
  const element = document.getElementById("toolbox-activities");
  const data = JSON.parse(element.dataset.application);

  ReactDOM.render(
    <Application>
      <ToolboxPage {...transformToolbox(data)} />
    </Application>,
    element
  );
});
